package kr.happyjob.study.mng.model;

import java.util.Date;

public class ClassInfoDto {
	
	//수강인원/강의목록
	private int class_No;
	private String class_Nm;
	private String loginID;
	private Date class_Startdate;
	private Date class_Enddate;
	private String class_Type;
	private String name;
	
	public ClassInfoDto() {
		super();
		// TODO Auto-generated constructor stub
	}

	public ClassInfoDto(int class_No, String class_Nm, String loginID, Date class_Startdate, Date class_Enddate,
			String class_Type, String name) {
		super();
		this.class_No = class_No;
		this.class_Nm = class_Nm;
		this.loginID = loginID;
		this.class_Startdate = class_Startdate;
		this.class_Enddate = class_Enddate;
		this.class_Type = class_Type;
		this.name = name;
	}

	public int getClass_No() {
		return class_No;
	}

	public void setClass_No(int class_No) {
		this.class_No = class_No;
	}

	public String getClass_Nm() {
		return class_Nm;
	}

	public void setClass_Nm(String class_Nm) {
		this.class_Nm = class_Nm;
	}

	public String getLoginID() {
		return loginID;
	}

	public void setLoginID(String loginID) {
		this.loginID = loginID;
	}

	public Date getClass_Startdate() {
		return class_Startdate;
	}

	public void setClass_Startdate(Date class_Startdate) {
		this.class_Startdate = class_Startdate;
	}

	public Date getClass_Enddate() {
		return class_Enddate;
	}

	public void setClass_Enddate(Date class_Enddate) {
		this.class_Enddate = class_Enddate;
	}

	public String getClass_Type() {
		return class_Type;
	}

	public void setClass_Type(String class_Type) {
		this.class_Type = class_Type;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	@Override
	public String toString() {
		return "ClassInfoDto [class_No=" + class_No + ", class_Nm=" + class_Nm + ", loginID=" + loginID
				+ ", class_Startdate=" + class_Startdate + ", class_Enddate=" + class_Enddate + ", class_Type="
				+ class_Type + ", name=" + name + "]";
	}
	
	
	
					
}
