package kr.happyjob.study.mng.model;

import java.util.Date;

public class HomeworkDto {

	private int hw_No;
	private int class_No;
	private String hw_Subject;
	private String hw_Content;
	private int no;
	private Date hw_Date;
	private String name;
	private String class_nm;
	private String file_Name;

	
	public HomeworkDto() {
		super();
		// TODO Auto-generated constructor stub
	}


	public HomeworkDto(int hw_No, int class_No, String hw_Subject, String hw_Content, int no, Date hw_Date, String name,
			String class_nm, String file_Name) {
		super();
		this.hw_No = hw_No;
		this.class_No = class_No;
		this.hw_Subject = hw_Subject;
		this.hw_Content = hw_Content;
		this.no = no;
		this.hw_Date = hw_Date;
		this.name = name;
		this.class_nm = class_nm;
		this.file_Name = file_Name;
	}


	public int getHw_No() {
		return hw_No;
	}


	public void setHw_No(int hw_No) {
		this.hw_No = hw_No;
	}


	public int getClass_No() {
		return class_No;
	}


	public void setClass_No(int class_No) {
		this.class_No = class_No;
	}


	public String getHw_Subject() {
		return hw_Subject;
	}


	public void setHw_Subject(String hw_Subject) {
		this.hw_Subject = hw_Subject;
	}


	public String getHw_Content() {
		return hw_Content;
	}


	public void setHw_Content(String hw_Content) {
		this.hw_Content = hw_Content;
	}


	public int getNo() {
		return no;
	}


	public void setNo(int no) {
		this.no = no;
	}


	public Date getHw_Date() {
		return hw_Date;
	}


	public void setHw_Date(Date hw_Date) {
		this.hw_Date = hw_Date;
	}


	public String getName() {
		return name;
	}


	public void setName(String name) {
		this.name = name;
	}


	public String getClass_nm() {
		return class_nm;
	}


	public void setClass_nm(String class_nm) {
		this.class_nm = class_nm;
	}


	public String getFile_Name() {
		return file_Name;
	}


	public void setFile_Name(String file_Name) {
		this.file_Name = file_Name;
	}


	@Override
	public String toString() {
		return "HomeworkDto [hw_No=" + hw_No + ", class_No=" + class_No + ", hw_Subject=" + hw_Subject + ", hw_Content="
				+ hw_Content + ", no=" + no + ", hw_Date=" + hw_Date + ", name=" + name + ", class_nm=" + class_nm
				+ ", file_Name=" + file_Name + "]";
	}
	

	
}
