package kr.happyjob.study.mng.model;

import java.util.Date;

public class HwSubmitDto {
	
	private int hw_Submit_No;
	private int hw_No;
	private int class_No;
	private String loginID;
	private String hw_Content;
	private int no;
	private Date hw_Date;
	public HwSubmitDto() {
		super();
		// TODO Auto-generated constructor stub
	}
	public HwSubmitDto(int hw_Submit_No, int hw_No, int class_No, String loginID, String hw_Content, int no,
			Date hw_Date) {
		super();
		this.hw_Submit_No = hw_Submit_No;
		this.hw_No = hw_No;
		this.class_No = class_No;
		this.loginID = loginID;
		this.hw_Content = hw_Content;
		this.no = no;
		this.hw_Date = hw_Date;
	}
	
	
	public HwSubmitDto(int hw_No, String loginID) {
		super();
		this.hw_No = hw_No;
		this.loginID = loginID;
	}
	public int getHw_Submit_No() {
		return hw_Submit_No;
	}
	public void setHw_Submit_No(int hw_Submit_No) {
		this.hw_Submit_No = hw_Submit_No;
	}
	public int getHw_No() {
		return hw_No;
	}
	public void setHw_No(int hw_No) {
		this.hw_No = hw_No;
	}
	public int getClass_No() {
		return class_No;
	}
	public void setClass_No(int class_No) {
		this.class_No = class_No;
	}
	public String getLoginID() {
		return loginID;
	}
	public void setLoginID(String loginID) {
		this.loginID = loginID;
	}
	public String getHw_Content() {
		return hw_Content;
	}
	public void setHw_Content(String hw_Content) {
		this.hw_Content = hw_Content;
	}
	public int getNo() {
		return no;
	}
	public void setNo(int no) {
		this.no = no;
	}
	public Date getHw_Date() {
		return hw_Date;
	}
	public void setHw_Date(Date hw_Date) {
		this.hw_Date = hw_Date;
	}
	@Override
	public String toString() {
		return "HwSubmitDto [hw_Submit_No=" + hw_Submit_No + ", hw_No=" + hw_No + ", class_No=" + class_No
				+ ", loginID=" + loginID + ", hw_Content=" + hw_Content + ", no=" + no + ", hw_Date=" + hw_Date + "]";
	}
	
	
	


}
